recuperar_categorias(Juego, Categorias) :-
    juego(Juego, Categorias, _, _, _, _, _).

%con findall busca todos los juegos que cumplen que Lista sea sublista de Categoria con el forall y el member, y esos juegos los guarda en Resultado. 
recuperar_juegos_con_categorias(Lista, Resultado) :-
    findall(Juego, (
        juego(Juego, Categorias, _, _, _, _, _),
        forall(member(Categoria, Lista), member(Categoria, Categorias))
    ), Resultado).

recuperar_imagen(Juego, Imagen) :-
    juego(Juego, _, _, Imagen, _, _, _).

recuperar_link(Juego, Link) :-
    juego(Juego, _, Link, _, _, _, _).

recuperar_sinopsis(Juego, Sinopsis) :-
    juego(Juego, _, _, _, Sinopsis, _, _).

element_exists(X, Result) :-
    categorias(Lista),
    (member(X, Lista) -> Result = "true" ; Result = "false").

exist_juego(Titulo, Result) :-
    (juego(Titulo, _, _, _, _, _, _) -> Result = "true" ; Result = "false").

recuperar_todas_las_categorias(Resultado) :-
    categorias(Resultado).

recuperar_Desarrolador(Juego, Desarrolador) :-
    juego(Juego, _, _, _, _, Desarrolador, _).

recuperar_Pal_Claves(Juego, Pal) :-
    juego(Juego, _, _, _, _, _, Pal).

% Regla para imprimir el juego en formato CSV y devolver el resultado
imprimir_juego(Nombre, Generos, Desarrollador, PalabrasClaves, Resultado) :-
    atomic_list_concat(Generos, ',', GenerosStr),
    atomic_list_concat(PalabrasClaves, ',', PalabrasClavesStr),
    format(atom(Resultado), '~w;~w;~w;~w', [Nombre, GenerosStr, Desarrollador, PalabrasClavesStr]).

imprimir_juego_unico(Nombre, Resultado) :-
    juego(Nombre, Generos, _, _, _, Desarrollador, PalabrasClaves),
    imprimir_juego(Nombre, Generos, Desarrollador, PalabrasClaves, Resultado).

% Regla para imprimir todos los juegos en formato CSV y devolver el resultado
imprimir_todos_los_juegos(Resultado) :-
    findall(Juego, (
        juego(Nombre, Generos, _, _, _, Desarrollador, PalabrasClaves),
        imprimir_juego(Nombre, Generos, Desarrollador, PalabrasClaves, Juego)
    ), Resultado).

% Regla para imprimir todos los juegos excepto los que estn en la lista de nombres y devolver el resultado
imprimir_todos_los_juegos_excepto_nombres(Nombres, Resultado) :-
    findall(Juego, (
        juego(Nombre, Generos, _, _, _, Desarrollador, PalabrasClaves),
        \+ member(Nombre, Nombres),
        imprimir_juego(Nombre, Generos, Desarrollador, PalabrasClaves, Juego)
    ), Resultado).

